export interface Destination {
  id: string;
  name: string;
  location: string;
  image: string;
  description: string;
  temperature: string;
  activities: string[];
  rating: number;
}

export interface TravelGuide {
  id: string;
  title: string;
  destination: string;
  duration: string;
  difficulty: string;
  image: string;
  highlights: string[];
  description: string;
}

import { Button } from './ui/button';
import { Snowflake } from 'lucide-react';

export const Hero = () => {
  return (
    <div className="relative h-[600px] flex items-center justify-center overflow-hidden">
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage: 'url(https://images.unsplash.com/photo-1551582045-6ec9c11d8697?w=1600&q=80)',
        }}
      >
        <div className="absolute inset-0 bg-gradient-to-b from-primary/30 via-primary/50 to-background" />
      </div>

      <div className="relative z-10 text-center px-4 max-w-4xl mx-auto">
        <div className="flex justify-center mb-6">
          <Snowflake className="w-16 h-16 text-accent animate-pulse" />
        </div>
        <h1 className="text-5xl md:text-7xl font-bold mb-6 text-foreground drop-shadow-lg">
          探索冰雪世界
        </h1>
        <p className="text-xl md:text-2xl mb-8 text-foreground/90 drop-shadow">
          发现中国最美冰雪景观，开启难忘的冬季之旅
        </p>
        <div className="flex flex-wrap gap-4 justify-center">
          <Button size="lg" className="text-lg px-8">
            探索景点
          </Button>
          <Button size="lg" variant="secondary" className="text-lg px-8">
            查看路书
          </Button>
        </div>
      </div>
    </div>
  );
};

import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { MapPin, Thermometer, Star } from 'lucide-react';
import { Destination } from '../types';

interface DestinationCardProps {
  destination: Destination;
}

export const DestinationCard = ({ destination }: DestinationCardProps) => {
  return (
    <Card className="overflow-hidden hover:shadow-xl transition-all duration-300 group">
      <div className="relative h-48 overflow-hidden">
        <img
          src={destination.image}
          alt={destination.name}
          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
        />
        <div className="absolute top-4 right-4 bg-card/95 backdrop-blur-sm px-3 py-1 rounded-full flex items-center gap-1">
          <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
          <span className="font-semibold">{destination.rating}</span>
        </div>
      </div>

      <CardHeader>
        <CardTitle className="text-xl">{destination.name}</CardTitle>
        <CardDescription className="flex items-center gap-1">
          <MapPin className="w-4 h-4" />
          {destination.location}
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-3">
        <p className="text-sm text-muted-foreground">{destination.description}</p>

        <div className="flex items-center gap-2 text-sm">
          <Thermometer className="w-4 h-4 text-accent" />
          <span className="font-medium">{destination.temperature}</span>
        </div>

        <div className="flex flex-wrap gap-2">
          {destination.activities.slice(0, 3).map((activity) => (
            <Badge key={activity} variant="secondary">
              {activity}
            </Badge>
          ))}
        </div>
      </CardContent>

      <CardFooter>
        <Button className="w-full">查看详情</Button>
      </CardFooter>
    </Card>
  );
};

import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { Clock, TrendingUp, MapIcon } from 'lucide-react';
import { TravelGuide } from '../types';

interface GuideCardProps {
  guide: TravelGuide;
}

export const GuideCard = ({ guide }: GuideCardProps) => {
  const getDifficultyColor = (difficulty: string) => {
    const colors = {
      '轻松': 'bg-green-500/10 text-green-700 dark:text-green-400',
      '中等': 'bg-yellow-500/10 text-yellow-700 dark:text-yellow-400',
      '挑战': 'bg-red-500/10 text-red-700 dark:text-red-400',
    };
    return colors[difficulty as keyof typeof colors] || colors['中等'];
  };

  return (
    <Card className="overflow-hidden hover:shadow-xl transition-all duration-300">
      <div className="relative h-56 overflow-hidden">
        <img
          src={guide.image}
          alt={guide.title}
          className="w-full h-full object-cover hover:scale-105 transition-transform duration-500"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent" />
        <div className="absolute bottom-4 left-4 right-4">
          <h3 className="text-white text-2xl font-bold drop-shadow-lg">{guide.title}</h3>
        </div>
      </div>

      <CardHeader>
        <div className="flex items-center justify-between">
          <CardDescription className="flex items-center gap-1">
            <MapIcon className="w-4 h-4" />
            {guide.destination}
          </CardDescription>
          <Badge className={getDifficultyColor(guide.difficulty)}>
            {guide.difficulty}
          </Badge>
        </div>
        <div className="flex items-center gap-4 text-sm text-muted-foreground">
          <div className="flex items-center gap-1">
            <Clock className="w-4 h-4" />
            {guide.duration}
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-3">
        <p className="text-sm text-muted-foreground">{guide.description}</p>

        <div className="space-y-2">
          <div className="flex items-center gap-2 text-sm font-semibold">
            <TrendingUp className="w-4 h-4 text-primary" />
            <span>行程亮点</span>
          </div>
          <ul className="space-y-1.5">
            {guide.highlights.map((highlight, index) => (
              <li key={index} className="text-sm text-muted-foreground flex gap-2">
                <span className="text-primary font-medium shrink-0">•</span>
                <span>{highlight}</span>
              </li>
            ))}
          </ul>
        </div>
      </CardContent>

      <CardFooter>
        <Button className="w-full" variant="outline">
          查看完整路书
        </Button>
      </CardFooter>
    </Card>
  );
};

